/// <reference types="node" />
import { Server as HttpServer } from 'http';
import { Server as HttpsServer } from 'https';
import { WorkerOptions } from './worker';
export interface ServerOptions extends WorkerOptions {
    clientName: string;
    server: HttpServer | HttpsServer;
    /**
     * @default 100MB
     */
    maxPayloadBytes?: number;
}
export default function createWebsocketServer({ clientName, server, maxPayloadBytes, ...workerOptions }: ServerOptions): {
    getClient(moduleID: string): Promise<string>;
};
